insert into students_completed_courses (student_id, course_id, units, ge_areas, from_transcript, letter_grading) (select stc.student_id, stc.course_id, stc.units, stc.ge_areas, stc.from_transcript, true from students_tentative_courses stc join courses on courses.id = stc.course_id and courses.term_year = 2016 and courses.term_month = 3 where not exists (select 1 from students_completed_courses scc where scc.student_id = stc.student_id and scc.course_id = stc.course_id));
